    },
    isMine: {
      type: Boolean
    }
  },
  components: {
    MessageBubble: MessageBubble,
    ElProgress: _Progress
  },
  computed: {
    fileName: function fileName() {
      return this.payload.fileName;
    },
    fileUrl: function fileUrl() {
      return this.payload.fileUrl;
    },
    size: function size() {
      var size = this.payload.fileSize;

      if (size > 1024) {
        if (size / 1024 > 1024) {
          return "".concat(this.toFixed(size / 1024 / 1024), " Mb");
        }

        return "".concat(this.toFixed(size / 1024), " Kb");
      }

      return "".concat(this.toFixed(size), "B");
    },
    showProgressBar: function showProgressBar() {
      return this.$parent.message.status === 'unSend';
    },
    percentage: function percentage() {
      return Math.floor((this.$parent.message.progress || 0) * 100);
    }
  },
  methods: {
    toFixed: function toFixed(number) {
      var precision = arguments.length > 1 && arguments[1] !== undefined ? arguments[1] : 2;
      return number.toFixed(precision);
    },
    downloadFile: function downloadFile() {
      var _this = this;

      // 浏览器支持fetch则用blob下载，避免浏览器点击a标签，跳转到新页面预览的行为
      if (window.fetch) {
        fetch(this.fileUrl).then(function (res) {
          return res.blob();
        }).then(function (blob) {
          var a = document.createElement('a');
          var url = window.URL.createObjectURL(blob);
          a.href = url;
          a.download = _this.fileName;
          a.click();
        });
      } else {
        var a = document.createElement('a');
        a.href = this.fileUrl;
        a.target = '_blank';
        a.download = this.filename;
        a.click();
      }
    }
  }
};
